import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Profile.css';
import ProjectHistory from './ProjectHistory';

function Profile({ user }) {
  const [activeTab, setActiveTab] = useState('profile');
  const [profile, setProfile] = useState(null);
  const [participationHistory, setParticipationHistory] = useState([]);
  const [loading, setLoading] = useState(true);
  const [editing, setEditing] = useState(false);
  const [formData, setFormData] = useState({});

  useEffect(() => {
    if (user) {
      fetchProfile();
      if (activeTab === 'history') {
        fetchParticipationHistory();
      }
    }
  }, [user, activeTab]);

  const fetchProfile = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:3000/api/profile', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      setProfile(response.data);
      setFormData(response.data);
      setLoading(false);
    } catch (error) {
      console.error('–û—à–∏–±–∫–∞ –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ –ø—Ä–æ—Ñ–∏–ª—è:', error);
      setLoading(false);
    }
  };

  const fetchParticipationHistory = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:3000/api/profile/participation-history', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      setParticipationHistory(response.data);
    } catch (error) {
      console.error('–û—à–∏–±–∫–∞ –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ –∏—Å—Ç–æ—Ä–∏–∏ —É—á–∞—Å—Ç–∏—è:', error);
    }
  };

  const handleSaveProfile = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      const response = await axios.put('http://localhost:3000/api/profile', formData, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      setProfile(response.data);
      setEditing(false);
      alert('–ü—Ä–æ—Ñ–∏–ª—å —É—Å–ø–µ—à–Ω–æ –æ–±–Ω–æ–≤–ª–µ–Ω!');
    } catch (error) {
      console.error('–û—à–∏–±–∫–∞ –ø—Ä–∏ –æ–±–Ω–æ–≤–ª–µ–Ω–∏–∏ –ø—Ä–æ—Ñ–∏–ª—è:', error);
      alert('–û—à–∏–±–∫–∞ –ø—Ä–∏ –æ–±–Ω–æ–≤–ª–µ–Ω–∏–∏ –ø—Ä–æ—Ñ–∏–ª—è');
    }
  };

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const generateCertificate = (project) => {
    const certificateText = `
      –°–ï–†–¢–ò–§–ò–ö–ê–¢ –í–û–õ–û–ù–¢–ï–†–ê

      –ù–∞—Å—Ç–æ—è—â–∏–º –ø–æ–¥—Ç–≤–µ—Ä–∂–¥–∞–µ—Ç—Å—è, —á—Ç–æ
      ${profile.firstName} ${profile.lastName}
      –ø—Ä–∏–Ω—è–ª(–∞) —É—á–∞—Å—Ç–∏–µ –≤ –ø—Ä–æ–µ–∫—Ç–µ:
      "${project.project.title}"

      –î–∞—Ç–∞ —É—á–∞—Å—Ç–∏—è: ${new Date(project.createdAt).toLocaleDateString('ru-RU')}

      –û—Ä–≥–∞–Ω–∏–∑–∞—Ç–æ—Ä: ${project.project.creator.firstName} ${project.project.creator.lastName}

      –ë–ª–∞–≥–æ–¥–∞—Ä–∏–º –∑–∞ –≤–∞—à –≤–∫–ª–∞–¥!  `;

    const blob = new Blob([certificateText], { type: 'text/plain' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `—Å–µ—Ä—Ç–∏—Ñ–∏–∫–∞—Ç_${project.project.title.replace(/\s+/g, '_')}.txt`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  if (loading) {
    return <div className="loading">–ó–∞–≥—Ä—É–∑–∫–∞ –ø—Ä–æ—Ñ–∏–ª—è...</div>;
  }

  return (
    <div className="profile">
      <div className="profile-header">
        <h1>–õ–∏—á–Ω—ã–π –∫–∞–±–∏–Ω–µ—Ç</h1>
        <div className="user-welcome">
          –î–æ–±—Ä–æ –ø–æ–∂–∞–ª–æ–≤–∞—Ç—å, {profile?.firstName}!
        </div>
      </div>

      {/* –í–ö–õ–ê–î–ö–ò - –û–ë–ù–û–í–õ–ï–ù–ù–ê–Ø –°–¢–†–£–ö–¢–£–†–ê */}
      <div className="profile-tabs-container">
        <div className="profile-tabs">
          <button
            className={`tab ${activeTab === 'profile' ? 'active' : ''}`}
            onClick={() => setActiveTab('profile')}
          >
            üìù –ü—Ä–æ—Ñ–∏–ª—å
          </button>
          <button
            className={`tab ${activeTab === 'history' ? 'active' : ''}`}
            onClick={() => setActiveTab('history')}
          >
            üìä –ò—Å—Ç–æ—Ä–∏—è —É—á–∞—Å—Ç–∏—è
          </button>
          <button
            className={`tab ${activeTab === 'applications' ? 'active' : ''}`}
            onClick={() => setActiveTab('applications')}
          >
            üì® –ú–æ–∏ –∑–∞—è–≤–∫–∏
          </button>
          <button
            className={`tab ${activeTab === 'projectHistory' ? 'active' : ''}`}
            onClick={() => setActiveTab('projectHistory')}
          >
            üìã –ò—Å—Ç–æ—Ä–∏—è –º–µ—Ä–æ–ø—Ä–∏—è—Ç–∏–π
          </button>
        </div>
      </div>

      <div className="profile-content">
        {activeTab === 'profile' && (
          <div className="profile-section">
            <div className="section-header">
              <h2>–õ–∏—á–Ω–∞—è –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è</h2>
              {!editing ? (
                <button
                  className="btn btn-primary"
                  onClick={() => setEditing(true)}
                >
                  ‚úèÔ∏è –†–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞—Ç—å
                </button>
              ) : (
                <div className="edit-actions">
                  <button
                    className="btn btn-success"
                    onClick={handleSaveProfile}
                  >
                    üíæ –°–æ—Ö—Ä–∞–Ω–∏—Ç—å
                  </button>
                  <button
                    className="btn btn-secondary"
                    onClick={() => {
                      setEditing(false);
                      setFormData(profile);
                    }}
                  >
                    ‚ùå –û—Ç–º–µ–Ω–∞
                  </button>
                </div>
              )}
            </div>

            {editing ? (
              <form className="profile-form">
                <div className="form-row">
                  <div className="form-group">
                    <label>–ò–º—è:</label>
                    <input
                      type="text"
                      name="firstName"
                      value={formData.firstName || ''}
                      onChange={handleInputChange}
                      required
                    />
                  </div>
                  <div className="form-group">
                    <label>–§–∞–º–∏–ª–∏—è:</label>
                    <input
                      type="text"
                      name="lastName"
                      value={formData.lastName || ''}
                      onChange={handleInputChange}
                      required
                    />
                  </div>
                </div>

                <div className="form-group">
                  <label>Email:</label>
                  <input
                    type="email"
                    value={profile.email}
                    disabled
                    className="disabled-input"
                  />
                  <small>Email –Ω–µ–ª—å–∑—è –∏–∑–º–µ–Ω–∏—Ç—å</small>
                </div>

                <div className="form-group">
  <label>–¢–µ–ª–µ—Ñ–æ–Ω:</label>
  <input
    type="tel"
    name="phone"
    value={formData.phone || ''}
    onChange={handleInputChange}
    placeholder="+7 (XXX) XXX-XX-XX"
    pattern="^\+?[0-9]{0,11}$"
    maxLength="12"
    onKeyPress={(e) => {
      // –†–∞–∑—Ä–µ—à–∞–µ–º —Ç–æ–ª—å–∫–æ —Ü–∏—Ñ—Ä—ã, Backspace, Delete, Tab –∏ +
      if (!/[0-9+]/.test(e.key) && 
          e.key !== 'Backspace' && 
          e.key !== 'Delete' && 
          e.key !== 'Tab') {
        e.preventDefault();
      }
    }}
  />
  <small>–§–æ—Ä–º–∞—Ç: +79991234567 (–º–∞–∫—Å–∏–º—É–º 11 —Ü–∏—Ñ—Ä –ø–æ—Å–ª–µ +)</small>
</div>

                <div className="form-group">
                  <label>–ù–∞–≤—ã–∫–∏:</label>
                  <textarea
                    name="skills"
                    value={formData.skills || ''}
                    onChange={handleInputChange}
                    placeholder="–ü–µ—Ä–µ—á–∏—Å–ª–∏—Ç–µ –≤–∞—à–∏ –Ω–∞–≤—ã–∫–∏ —á–µ—Ä–µ–∑ –∑–∞–ø—è—Ç—É—é"
                    rows="3"
                  />
                </div>

                <div className="form-group">
                  <label>–ò–Ω—Ç–µ—Ä–µ—Å—ã:</label>
                  <textarea
                    name="interests"
                    value={formData.interests || ''}
                    onChange={handleInputChange}
                    placeholder="–ü–µ—Ä–µ—á–∏—Å–ª–∏—Ç–µ –≤–∞—à–∏ –∏–Ω—Ç–µ—Ä–µ—Å—ã —á–µ—Ä–µ–∑ –∑–∞–ø—è—Ç—É—é"
                    rows="3"
                  />
                </div>

                <div className="form-group">
                  <label>–û —Å–µ–±–µ:</label>
                  <textarea
                    name="bio"
                    value={formData.bio || ''}
                    onChange={handleInputChange}
                    placeholder="–†–∞—Å—Å–∫–∞–∂–∏—Ç–µ –æ —Å–µ–±–µ, —Å–≤–æ–µ–º –æ–ø—ã—Ç–µ –≤–æ–ª–æ–Ω—Ç–µ—Ä—Å—Ç–≤–∞"
                    rows="4"
                  />
                </div>
              </form>
            ) : (
              <div className="profile-info">
                <div className="info-grid">
                  <div className="info-item">
                    <strong>–ò–º—è:</strong>
                    <span>{profile.firstName} {profile.lastName}</span>
                  </div>
                  <div className="info-item">
                    <strong>Email:</strong>
                    <span>{profile.email}</span>
                  </div>
                  <div className="info-item">
                    <strong>–¢–µ–ª–µ—Ñ–æ–Ω:</strong>
                    <span>{profile.phone || '–ù–µ —É–∫–∞–∑–∞–Ω'}</span>
                  </div>
                  <div className="info-item">
                    <strong>–ù–∞–≤—ã–∫–∏:</strong>
                    <span>{profile.skills || '–ù–µ —É–∫–∞–∑–∞–Ω—ã'}</span>
                  </div>
                  <div className="info-item">
                    <strong>–ò–Ω—Ç–µ—Ä–µ—Å—ã:</strong>
                    <span>{profile.interests || '–ù–µ —É–∫–∞–∑–∞–Ω—ã'}</span>
                  </div>
                  <div className="info-item full-width">
                    <strong>–û —Å–µ–±–µ:</strong>
                    <span>{profile.bio || '–ù–µ —É–∫–∞–∑–∞–Ω–æ'}</span>
                  </div>
                  <div className="info-item">
                    <strong>–î–∞—Ç–∞ —Ä–µ–≥–∏—Å—Ç—Ä–∞—Ü–∏–∏:</strong>
                    <span>{new Date(profile.createdAt).toLocaleDateString('ru-RU')}</span>
                  </div>
                </div>
              </div>
            )}
          </div>
        )}

        {activeTab === 'history' && (
          <div className="profile-section">
            <h2>–ò—Å—Ç–æ—Ä–∏—è —É—á–∞—Å—Ç–∏—è</h2>
            {participationHistory.length === 0 ? (
              <div className="empty-state">
                <p>–£ –≤–∞—Å –ø–æ–∫–∞ –Ω–µ—Ç –∑–∞–≤–µ—Ä—à–µ–Ω–Ω—ã—Ö –ø—Ä–æ–µ–∫—Ç–æ–≤</p>
                <p>–ü–æ–¥–∞–≤–∞–π—Ç–µ –∑–∞—è–≤–∫–∏ –Ω–∞ –ø—Ä–æ–µ–∫—Ç—ã –∏ —É—á–∞—Å—Ç–≤—É–π—Ç–µ –≤ –≤–æ–ª–æ–Ω—Ç–µ—Ä—Å–∫–æ–π –¥–µ—è—Ç–µ–ª—å–Ω–æ—Å—Ç–∏!</p>
              </div>
            ) : (
              <div className="history-list">
                {participationHistory.map(participation => (
                  <div key={participation.id} className="history-item">
                    <div className="history-content">
                      <h3>{participation.project.title}</h3>
                      <p>{participation.project.description}</p>
                      <div className="history-meta">
                        <span>–û—Ä–≥–∞–Ω–∏–∑–∞—Ç–æ—Ä: {participation.project.creator.firstName} {participation.project.creator.lastName}</span>
                        <span>–î–∞—Ç–∞ —É—á–∞—Å—Ç–∏—è: {new Date(participation.createdAt).toLocaleDateString('ru-RU')}</span>
                      </div>
                    </div>
                    <button
                      className="btn btn-primary"
                      onClick={() => generateCertificate(participation)}
                    >
                      üìÑ –°–∫–∞—á–∞—Ç—å —Å–µ—Ä—Ç–∏—Ñ–∏–∫–∞—Ç
                    </button>
                  </div>
                ))}
              </div>
            )}
          </div>
        )}

        {activeTab === 'applications' && (
          <div className="profile-section">
            <h2>–ú–æ–∏ –∑–∞—è–≤–∫–∏</h2>
