import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ProjectFilters from './ProjectFilters';
import './ProjectList.css';

function ProjectList({ user }) {
  const [projects, setProjects] = useState([]);
  const [filteredProjects, setFilteredProjects] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filters, setFilters] = useState({
    search: '',
    projectType: '',
    location: '',
    dateFrom: '',
    dateTo: ''
  });
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [projectToDelete, setProjectToDelete] = useState(null);
  const [editingProject, setEditingProject] = useState(null);

  useEffect(() => {
    fetchProjects();
    
    // ÐÐ²Ñ‚Ð¾Ð¼Ð°Ñ‚Ð¸Ñ‡ÐµÑÐºÐ¾Ðµ Ð¾Ð±Ð½Ð¾Ð²Ð»ÐµÐ½Ð¸Ðµ ÐºÐ°Ð¶Ð´Ñ‹Ðµ 30 ÑÐµÐºÑƒÐ½Ð´
    const interval = setInterval(fetchProjects, 30000);
    
    return () => clearInterval(interval);
  }, []);

  const fetchProjects = async (currentFilters = filters) => {
  try {
    setLoading(true);
    
    // Ð¡Ð¾Ð·Ð´Ð°ÐµÐ¼ Ð¿Ð°Ñ€Ð°Ð¼ÐµÑ‚Ñ€Ñ‹ Ð·Ð°Ð¿Ñ€Ð¾ÑÐ°
    const params = new URLSearchParams();
    if (currentFilters.search) params.append('search', currentFilters.search);
    if (currentFilters.projectType) params.append('projectType', currentFilters.projectType);
    if (currentFilters.location) params.append('location', currentFilters.location);
    if (currentFilters.dateFrom) params.append('dateFrom', currentFilters.dateFrom);
    if (currentFilters.dateTo) params.append('dateTo', currentFilters.dateTo);
    if (currentFilters.status) params.append('status', currentFilters.status);

    console.log('ÐŸÐ°Ñ€Ð°Ð¼ÐµÑ‚Ñ€Ñ‹ Ð·Ð°Ð¿Ñ€Ð¾ÑÐ°:', params.toString()); // Ð”Ð»Ñ Ð¾Ñ‚Ð»Ð°Ð´ÐºÐ¸

    const response = await axios.get(`http://localhost:3000/api/projects?${params}`);
    
    setProjects(response.data);
    setFilteredProjects(response.data);
