import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Загружаем проекты с бэкенда
    axios.get('http://localhost:3000/api/projects')
      .then(response => {
        setProjects(response.data);
        setLoading(false);
      })
      .catch(error => {
        console.error('Ошибка при загрузке проектов:', error);
        setLoading(false);
      });
  }, []);

  if (loading) {
    return <div className="App">Загрузка проектов...</div>;
  }

  return (
    <div className="App">
      <header className="App-header">
        <h1>Волонтерские проекты</h1>
      </header>
      <main>
        <div className="projects-list">
          {projects.map(project => (
            <div key={project.id} className="project-card">
              <h2>{project.title}</h2>
              <p>{project.description}</p>
              <div className="project-meta">
                <span>Статус: {project.status}</span>
                <span>Создатель: {project.creator.firstName} {project.creator.lastName}</span>
                <span>Дата: {new Date(project.createdAt).toLocaleDateString()}</span>
              </div>
              <button className="apply-btn">Подать заявку</button>
            </div>
          ))}
        </div>
      </main>
    </div>
  );
}

export default App;
