import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Profile.css';
import OrganizerStats from './OrganizerStats';
import DraftProjects from './DraftProjects';

function Profile({ user }) {
  const [activeTab, setActiveTab] = useState('profile');
  const [profile, setProfile] = useState(null);
  const [participationHistory, setParticipationHistory] = useState([]);
  const [loading, setLoading] = useState(false);
  const [editing, setEditing] = useState(false);
  const [formData, setFormData] = useState({});
  const [showDeleteConfirm, setShowDeleteConfirm] = useState(false);

  // Ğ˜ÑĞ¿Ğ¾Ğ»ÑŒĞ·ÑƒĞµĞ¼ Ğ´Ğ°Ğ½Ğ½Ñ‹Ğµ Ğ¸Ğ· user, ĞºĞ¾Ñ‚Ğ¾Ñ€Ñ‹Ğµ ÑƒĞ¶Ğµ ĞµÑÑ‚ÑŒ Ğ¿Ğ¾ÑĞ»Ğµ Ğ»Ğ¾Ğ³Ğ¸Ğ½Ğ°
  useEffect(() => {
  const fetchUserProfile = async () => {
    if (user) {
      try {
        const token = localStorage.getItem('token');
        if (!token) return;

        // Ğ—Ğ°Ğ³Ñ€ÑƒĞ¶Ğ°ĞµĞ¼ Ğ°ĞºÑ‚ÑƒĞ°Ğ»ÑŒĞ½Ñ‹Ğµ Ğ´Ğ°Ğ½Ğ½Ñ‹Ğµ Ğ¿Ğ¾Ğ»ÑŒĞ·Ğ¾Ğ²Ğ°Ñ‚ĞµĞ»Ñ Ğ¸Ğ· Ğ±Ğ°Ğ·Ñ‹
        const response = await axios.get('http://localhost:3000/api/auth/me', {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });

        const userData = response.data;
        console.log('ğŸ“¥ Ğ—Ğ°Ğ³Ñ€ÑƒĞ¶ĞµĞ½ Ğ¿Ñ€Ğ¾Ñ„Ğ¸Ğ»ÑŒ Ğ¸Ğ· Ğ±Ğ°Ğ·Ñ‹:', userData);

        const userProfile = {
          firstName: userData.firstName,
          lastName: userData.lastName,
          email: userData.email,
          role: userData.role,
          phone: userData.phone || '',
          skills: userData.skills || '',
          interests: userData.interests || '',
          bio: userData.bio || '',
          createdAt: userData.createdAt
        };

        setProfile(userProfile);
        setFormData(userProfile);

        // ĞĞ±Ğ½Ğ¾Ğ²Ğ»ÑĞµĞ¼ Ğ´Ğ°Ğ½Ğ½Ñ‹Ğµ Ğ² localStorage
        localStorage.setItem('user', JSON.stringify(userData));

      } catch (error) {
        console.error('ĞÑˆĞ¸Ğ±ĞºĞ° Ğ¿Ñ€Ğ¸ Ğ·Ğ°Ğ³Ñ€ÑƒĞ·ĞºĞµ Ğ¿Ñ€Ğ¾Ñ„Ğ¸Ğ»Ñ:', error);
        // Ğ•ÑĞ»Ğ¸ Ğ½Ğµ ÑƒĞ´Ğ°Ğ»Ğ¾ÑÑŒ Ğ·Ğ°Ğ³Ñ€ÑƒĞ·Ğ¸Ñ‚ÑŒ Ğ¸Ğ· Ğ±Ğ°Ğ·Ñ‹, Ğ¸ÑĞ¿Ğ¾Ğ»ÑŒĞ·ÑƒĞµĞ¼ Ğ´Ğ°Ğ½Ğ½Ñ‹Ğµ Ğ¸Ğ· localStorage
        const userProfile = {
          firstName: user.firstName,
          lastName: user.lastName,
          email: user.email,
          role: user.role,
          phone: user.phone || '',
          skills: user.skills || '',
          interests: user.interests || '',
          bio: user.bio || '',
          createdAt: user.createdAt || new Date().toISOString()
        };
        setProfile(userProfile);
        setFormData(userProfile);
      }
    }
  };

  fetchUserProfile();
}, [user]);
  useEffect(() => {
    if (user && activeTab === 'history') {
      fetchParticipationHistory();
    }
  }, [activeTab, user]);

  const fetchParticipationHistory = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) return;
      const response = await axios.get('http://localhost:3000/api/profile/participation-history', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      setParticipationHistory(response.data);
    } catch (error) {
      console.error('ĞÑˆĞ¸Ğ±ĞºĞ° Ğ¿Ñ€Ğ¸ Ğ·Ğ°Ğ³Ñ€ÑƒĞ·ĞºĞµ Ğ¸ÑÑ‚Ğ¾Ñ€Ğ¸Ğ¸ ÑƒÑ‡Ğ°ÑÑ‚Ğ¸Ñ:', error);
    }
  };

 const handleSaveProfile = async (e) => {
  e.preventDefault();
  
  try {
    const token = localStorage.getItem('token');
    if (!token) {
      alert('Ğ¢Ñ€ĞµĞ±ÑƒĞµÑ‚ÑÑ Ğ°Ğ²Ñ‚Ğ¾Ñ€Ğ¸Ğ·Ğ°Ñ†Ğ¸Ñ');
      return;
    }

    const response = await axios.put('http://localhost:3000/api/profile', {
      firstName: formData.firstName,
      lastName: formData.lastName,
      phone: formData.phone,
      skills: formData.skills,
      interests: formData.interests,
      bio: formData.bio
    }, {
      headers: {
        'Authorization': `Bearer ${token}`
      }
    });

    console.log('âœ… ĞÑ‚Ğ²ĞµÑ‚ Ğ¾Ñ‚ ÑĞµÑ€Ğ²ĞµÑ€Ğ°:', response.data);

    // ĞĞ‘ĞĞĞ’Ğ›Ğ¯Ğ•Ğœ Ğ’Ğ¡Ğ• ĞŸĞĞ›Ğ¯ Ğ’ LOCALSTORAGE
    const updatedUser = {
      ...user,
      firstName: response.data.firstName,
      lastName: response.data.lastName,
      phone: response.data.phone,
      skills: response.data.skills,
      interests: response.data.interests,
      bio: response.data.bio
    };

    localStorage.setItem('user', JSON.stringify(updatedUser));
    
    // ĞĞ‘ĞĞĞ’Ğ›Ğ¯Ğ•Ğœ Ğ¡ĞĞ¡Ğ¢ĞĞ¯ĞĞ˜Ğ• ĞŸĞ ĞĞ¤Ğ˜Ğ›Ğ¯
    setProfile(response.data);
    console.log('ğŸ”„ profile Ğ¿Ğ¾ÑĞ»Ğµ Ğ¾Ğ±Ğ½Ğ¾Ğ²Ğ»ĞµĞ½Ğ¸Ñ:', profile);
console.log('ğŸ”„ ĞĞ°Ğ²Ñ‹ĞºĞ¸ Ğ² profile Ğ¿Ğ¾ÑĞ»Ğµ Ğ¾Ğ±Ğ½Ğ¾Ğ²Ğ»ĞµĞ½Ğ¸Ñ:', profile.skills);
    setEditing(false);
    
    alert('ĞŸÑ€Ğ¾Ñ„Ğ¸Ğ»ÑŒ ÑƒÑĞ¿ĞµÑˆĞ½Ğ¾ Ğ¾Ğ±Ğ½Ğ¾Ğ²Ğ»ĞµĞ½!');

  } catch (error) {
    console.error('âŒ ĞÑˆĞ¸Ğ±ĞºĞ° Ğ¿Ñ€Ğ¸ Ğ¾Ğ±Ğ½Ğ¾Ğ²Ğ»ĞµĞ½Ğ¸Ğ¸ Ğ¿Ñ€Ğ¾Ñ„Ğ¸Ğ»Ñ:', error);
    alert('ĞÑˆĞ¸Ğ±ĞºĞ° Ğ¿Ñ€Ğ¸ Ğ¾Ğ±Ğ½Ğ¾Ğ²Ğ»ĞµĞ½Ğ¸Ğ¸ Ğ¿Ñ€Ğ¾Ñ„Ğ¸Ğ»Ñ: ' + error.message);
  }
};

const handleInputChange = (e) => {
  const { name, value } = e.target;
  /*console.log(`ğŸ” ĞŸĞ¾Ğ»Ğµ "${name}" Ğ¸Ğ·Ğ¼ĞµĞ½ÑĞµÑ‚ÑÑ Ğ½Ğ°: "${value}"`);*/
  
  // Ğ¢ĞĞ›Ğ¬ĞšĞ Ğ´Ğ»Ñ Ñ‚ĞµĞ»ĞµÑ„Ğ¾Ğ½Ğ° Ğ¿Ñ€Ğ¸Ğ¼ĞµĞ½ÑĞµĞ¼ ÑĞ¿ĞµÑ†Ğ¸Ğ°Ğ»ÑŒĞ½ÑƒÑ Ğ»Ğ¾Ğ³Ğ¸ĞºÑƒ
  if (name === 'phone') {
    let cleanedValue = value.replace(/[^\d+]/g, '');
    
    if (cleanedValue.startsWith('8')) {
      cleanedValue = '+7' + cleanedValue.substring(1);
    } else if (cleanedValue.startsWith('7') && !cleanedValue.startsWith('+7')) {
      cleanedValue = '+7' + cleanedValue.substring(1);
    } else if (!cleanedValue.startsWith('+')) {
      cleanedValue = '+7' + cleanedValue;
    }
    
    if (cleanedValue.length > 12) {
      cleanedValue = cleanedValue.substring(0, 12);
    }
    
    setFormData(prevFormData => ({
      ...prevFormData,
      [name]: cleanedValue
    }));
  } else {
    // Ğ”Ğ»Ñ Ğ’Ğ¡Ğ•Ğ¥ Ğ¾ÑÑ‚Ğ°Ğ»ÑŒĞ½Ñ‹Ñ… Ğ¿Ğ¾Ğ»ĞµĞ¹ (Ğ½Ğ°Ğ²Ñ‹ĞºĞ¸, Ğ¸Ğ½Ñ‚ĞµÑ€ĞµÑÑ‹, bio) - Ğ¿Ñ€Ğ¾ÑÑ‚Ğ¾ ÑĞ¾Ñ…Ñ€Ğ°Ğ½ÑĞµĞ¼ Ğ·Ğ½Ğ°Ñ‡ĞµĞ½Ğ¸Ğµ
    setFormData(prevFormData => ({
      ...prevFormData,
      [name]: value
    }));
  }
  /*console.log('ğŸ“Š formData Ğ¿Ğ¾ÑĞ»Ğµ Ğ¸Ğ·Ğ¼ĞµĞ½ĞµĞ½Ğ¸Ñ:', formData);*/
};

  const handleDeleteAccount = async () => {
    if (!window.confirm('Ğ’Ñ‹ ÑƒĞ²ĞµÑ€ĞµĞ½Ñ‹, Ñ‡Ñ‚Ğ¾ Ñ…Ğ¾Ñ‚Ğ¸Ñ‚Ğµ ÑƒĞ´Ğ°Ğ»Ğ¸Ñ‚ÑŒ Ğ°ĞºĞºĞ°ÑƒĞ½Ñ‚? Ğ­Ñ‚Ğ¾ Ğ´ĞµĞ¹ÑÑ‚Ğ²Ğ¸Ğµ Ğ½ĞµĞ»ÑŒĞ·Ñ Ğ¾Ñ‚Ğ¼ĞµĞ½Ğ¸Ñ‚ÑŒ. Ğ’ÑĞµ Ğ²Ğ°ÑˆĞ¸ Ğ¿Ñ€Ğ¾ĞµĞºÑ‚Ñ‹ Ğ¸ Ğ·Ğ°ÑĞ²ĞºĞ¸ Ğ±ÑƒĞ´ÑƒÑ‚ ÑƒĞ´Ğ°Ğ»ĞµĞ½Ñ‹.')) {
      return;
    }

    try {
      const token = localStorage.getItem('token');
      await axios.delete('http://localhost:3000/api/auth/account', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      alert('ĞĞºĞºĞ°ÑƒĞ½Ñ‚ ÑƒÑĞ¿ĞµÑˆĞ½Ğ¾ ÑƒĞ´Ğ°Ğ»ĞµĞ½');
      localStorage.removeItem('token');
      localStorage.removeItem('user');
      window.location.href = '/';
    } catch (error) {
      console.error('ĞÑˆĞ¸Ğ±ĞºĞ° Ğ¿Ñ€Ğ¸ ÑƒĞ´Ğ°Ğ»ĞµĞ½Ğ¸Ğ¸ Ğ°ĞºĞºĞ°ÑƒĞ½Ñ‚Ğ°:', error);
      alert(error.response?.data?.error || 'ĞĞµ ÑƒĞ´Ğ°Ğ»Ğ¾ÑÑŒ ÑƒĞ´Ğ°Ğ»Ğ¸Ñ‚ÑŒ Ğ°ĞºĞºĞ°ÑƒĞ½Ñ‚');
    }
  };

  const generateCertificate = (project) => {
    if (!profile) {
      alert('ĞĞµ ÑƒĞ´Ğ°Ğ»Ğ¾ÑÑŒ ÑĞ¾Ğ·Ğ´Ğ°Ñ‚ÑŒ ÑĞµÑ€Ñ‚Ğ¸Ñ„Ğ¸ĞºĞ°Ñ‚: Ğ´Ğ°Ğ½Ğ½Ñ‹Ğµ Ğ¿Ñ€Ğ¾Ñ„Ğ¸Ğ»Ñ Ğ½Ğµ Ğ·Ğ°Ğ³Ñ€ÑƒĞ¶ĞµĞ½Ñ‹');
      return;
    }

    const certificateText = `
      Ğ¡Ğ•Ğ Ğ¢Ğ˜Ğ¤Ğ˜ĞšĞĞ¢ Ğ’ĞĞ›ĞĞĞ¢Ğ•Ğ Ğ
      ĞĞ°ÑÑ‚Ğ¾ÑÑ‰Ğ¸Ğ¼ Ğ¿Ğ¾Ğ´Ñ‚Ğ²ĞµÑ€Ğ¶Ğ´Ğ°ĞµÑ‚ÑÑ, Ñ‡Ñ‚Ğ¾
      ${profile.firstName} ${profile.lastName}
      Ğ¿Ñ€Ğ¸Ğ½ÑĞ»(Ğ°) ÑƒÑ‡Ğ°ÑÑ‚Ğ¸Ğµ Ğ² Ğ¿Ñ€Ğ¾ĞµĞºÑ‚Ğµ:
      "${project.project.title}"
      Ğ”Ğ°Ñ‚Ğ° ÑƒÑ‡Ğ°ÑÑ‚Ğ¸Ñ: ${new Date(project.createdAt).toLocaleDateString('ru-RU')}
      ĞÑ€Ğ³Ğ°Ğ½Ğ¸Ğ·Ğ°Ñ‚Ğ¾Ñ€: ${project.project.creator.firstName} ${project.project.creator.lastName}
      Ğ‘Ğ»Ğ°Ğ³Ğ¾Ğ´Ğ°Ñ€Ğ¸Ğ¼ Ğ·Ğ° Ğ²Ğ°Ñˆ Ğ²ĞºĞ»Ğ°Ğ´!  `;

    const blob = new Blob([certificateText], { type: 'text/plain' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `ÑĞµÑ€Ñ‚Ğ¸Ñ„Ğ¸ĞºĞ°Ñ‚_${project.project.title.replace(/\s+/g, '_')}.txt`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };
const formatPhoneDisplay = (phone) => {
  if (!phone) return 'ĞĞµ ÑƒĞºĞ°Ğ·Ğ°Ğ½';
  
  const cleaned = phone.replace(/\D/g, '');
  
  if (cleaned.length === 11 && (cleaned.startsWith('7') || cleaned.startsWith('8'))) {
    const match = cleaned.match(/^[78]?(\d{3})(\d{3})(\d{2})(\d{2})$/);
    if (match) {
      return `+7 (${match[1]}) ${match[2]}-${match[3]}-${match[4]}`;
    }
  }
  
  return phone;
};

// Ğ’ Ñ„ÑƒĞ½ĞºÑ†Ğ¸Ğ¸, ĞºĞ¾Ñ‚Ğ¾Ñ€Ğ°Ñ Ğ²ĞºĞ»ÑÑ‡Ğ°ĞµÑ‚ Ñ€ĞµĞ´Ğ°ĞºÑ‚Ğ¸Ñ€Ğ¾Ğ²Ğ°Ğ½Ğ¸Ğµ:
const handleEditStart = () => {
  console.log('ğŸ” Ğ”Ğ°Ğ½Ğ½Ñ‹Ğµ Ğ¿Ñ€Ğ¾Ñ„Ğ¸Ğ»Ñ Ğ¿ĞµÑ€ĞµĞ´ Ñ€ĞµĞ´Ğ°ĞºÑ‚Ğ¸Ñ€Ğ¾Ğ²Ğ°Ğ½Ğ¸ĞµĞ¼:', profile);
  console.log('ğŸ” formData Ğ¿ĞµÑ€ĞµĞ´ Ñ€ĞµĞ´Ğ°ĞºÑ‚Ğ¸Ñ€Ğ¾Ğ²Ğ°Ğ½Ğ¸ĞµĞ¼:', formData);
  setEditing(true);
  // Ğ£Ğ±ĞµĞ´Ğ¸Ñ‚ĞµÑÑŒ, Ñ‡Ñ‚Ğ¾ formData ÑĞ¾Ğ´ĞµÑ€Ğ¶Ğ¸Ñ‚ Ğ°ĞºÑ‚ÑƒĞ°Ğ»ÑŒĞ½Ñ‹Ğµ Ğ´Ğ°Ğ½Ğ½Ñ‹Ğµ Ğ¿Ñ€Ğ¾Ñ„Ğ¸Ğ»Ñ
  setFormData({
    firstName: profile.firstName || '',
    lastName: profile.lastName || '',
    phone: profile.phone || '',
    skills: profile.skills || '',
    interests: profile.interests || '',
    bio: profile.bio || ''
  });
};
  if (!user) {
    return (
      <div className="error-container">
        <div className="error">
          <h2>ĞÑˆĞ¸Ğ±ĞºĞ° Ğ·Ğ°Ğ³Ñ€ÑƒĞ·ĞºĞ¸</h2>
          <p>ĞŸĞ¾Ğ»ÑŒĞ·Ğ¾Ğ²Ğ°Ñ‚ĞµĞ»ÑŒ Ğ½Ğµ Ğ°Ğ²Ñ‚Ğ¾Ñ€Ğ¸Ğ·Ğ¾Ğ²Ğ°Ğ½</p>
          <div className="error-actions">
            <button
              onClick={() => window.location.href = '/login'}
              className="btn btn-primary"
            >
              Ğ’Ğ¾Ğ¹Ñ‚Ğ¸
            </button>
          </div>
        </div>
      </div>
    );
  }

  if (!profile) {
    return <div className="loading">ĞŸĞ¾Ğ´Ğ³Ğ¾Ñ‚Ğ¾Ğ²ĞºĞ° Ğ¿Ñ€Ğ¾Ñ„Ğ¸Ğ»Ñ...</div>;
  }

  return (
    <div className="profile">
      <div className="profile-header">
        <h1>Ğ›Ğ¸Ñ‡Ğ½Ñ‹Ğ¹ ĞºĞ°Ğ±Ğ¸Ğ½ĞµÑ‚</h1>
      </div>

      <div className="profile-tabs-container">
        <div className="profile-tabs">
          <button
            className={`tab ${activeTab === 'profile' ? 'active' : ''}`}
            onClick={() => setActiveTab('profile')}
          >
            ğŸ“ ĞŸÑ€Ğ¾Ñ„Ğ¸Ğ»ÑŒ
          </button>
          <button
            className={`tab ${activeTab === 'history' ? 'active' : ''}`}
            onClick={() => setActiveTab('history')}
          >
            ğŸ“Š Ğ˜ÑÑ‚Ğ¾Ñ€Ğ¸Ñ ÑƒÑ‡Ğ°ÑÑ‚Ğ¸Ñ
          </button>
          <button
            className={`tab ${activeTab === 'applications' ? 'active' : ''}`}
            onClick={() => setActiveTab('applications')}
          >
            ğŸ“¨ ĞœĞ¾Ğ¸ Ğ·Ğ°ÑĞ²ĞºĞ¸
          </button>
          {user && user.role === 'organizer' && (
            <button
              className={`tab ${activeTab === 'stats' ? 'active' : ''}`}
              onClick={() => setActiveTab('stats')}
            >
              ğŸ“Š Ğ¡Ñ‚Ğ°Ñ‚Ğ¸ÑÑ‚Ğ¸ĞºĞ°
            </button>
          )}
          {user && user.role === 'organizer' && (
            <button
              className={`tab ${activeTab === 'drafts' ? 'active' : ''}`}
              onClick={() => setActiveTab('drafts')}
            >
              ğŸ“‹ Ğ§ĞµÑ€Ğ½Ğ¾Ğ²Ğ¸ĞºĞ¸
            </button>
          )}
        </div>
      </div>

      <div className="profile-content">
        {activeTab === 'profile' && (
          <div className="profile-section">
            <div className="section-header">
              <h2>Ğ›Ğ¸Ñ‡Ğ½Ğ°Ñ Ğ¸Ğ½Ñ„Ğ¾Ñ€Ğ¼Ğ°Ñ†Ğ¸Ñ</h2>
              {!editing ? (
                <button
                  className="btn btn-primary"
                  onClick={handleEditStart} 
                >
                  âœï¸ Ğ ĞµĞ´Ğ°ĞºÑ‚Ğ¸Ñ€Ğ¾Ğ²Ğ°Ñ‚ÑŒ
