              />
            </div>
          </div>

          <div className="form-row">
            <div className="form-group">
              <label>–î–∞—Ç–∞ –Ω–∞—á–∞–ª–∞:</label>
              <input
                type="date"
                name="startDate"
                value={formData.startDate}
                onChange={handleChange}
                style={{ width: '100%', padding: '0.5rem', marginBottom: '1rem' }}
