import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './ProjectList.css';

function ProjectList({ user }) {
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchProjects = async () => {
      try {
        const response = await axios.get('http://localhost:3000/api/projects');
        setProjects(response.data);
        setLoading(false);
      } catch (error) {
        console.error('Ошибка при загрузке проектов:', error);
        setLoading(false);
      }
    };

    fetchProjects();
  }, []);

  const handleApply = async (projectId) => {
    if (!user) {
      alert('Для подачи заявки необходимо войти в систему');
      return;
    }

    try {
      const token = localStorage.getItem('token');
      const message = prompt('Напишите сообщение организатору (необязательно):');
      
      await axios.post(
        `http://localhost:3000/api/projects/${projectId}/applications`,
        { message: message || '' },
        {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        }
      );
      
      alert('Заявка успешно подана!');
    } catch (error) {
      alert(error.response?.data?.error || 'Ошибка при подаче заявки');
    }
  };

  if (loading) {
    return <div className="loading">Загрузка проектов...</div>;
  }

  return (
    <div className="project-list">
      <h1>Волонтерские проекты</h1>
      <div className="projects-grid">
        {projects.map(project => (
          <div key={project.id} className="project-card">
            <h2>{project.title}</h2>
            <p>{project.description}</p>
            <div className="project-meta">
              <span>Создатель: {project.creator.firstName} {project.creator.lastName}</span>
              <span>Дата: {new Date(project.createdAt).toLocaleDateString()}</span>
              <span>Статус: {project.status}</span>
            </div>
            {user && user.role === 'volunteer' && (
              <button 
                className="apply-btn"
                onClick={() => handleApply(project.id)}
              >
                Подать заявку
              </button>
            )}
            {user && user.id === project.createdBy && (
              <button 
                className="view-applications-btn"
                onClick={() => window.location.href = `/project-applications/${project.id}`}
              >
                Посмотреть заявки
              </button>
            )}
          </div>
        ))}
      </div>
    </div>
  );
}

export default ProjectList;
