import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './ProjectList.css';

function ProjectList({ user }) {
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [projectToDelete, setProjectToDelete] = useState(null);
  const [editingProject, setEditingProject] = useState(null);

  useEffect(() => {
    fetchProjects();
  }, []);

  const fetchProjects = async () => {
    try {
      const response = await axios.get('http://localhost:3000/api/projects');
      setProjects(response.data);
      setLoading(false);
    } catch (error) {
      console.error('Ошибка при загрузке проектов:', error);
      setLoading(false);
    }
  };

  const handleApply = async (projectId) => {
    if (!user) {
      alert('Для подачи заявки необходимо войти в систему');
      return;
    }

    try {
      const token = localStorage.getItem('token');
      const message = prompt('Напишите сообщение организатору (необязательно):');
      
      await axios.post(
        `http://localhost:3000/api/projects/${projectId}/applications`,
        { message: message || '' },
        {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        }
      );
      
      alert('Заявка успешно подана!');
    } catch (error) {
      alert(error.response?.data?.error || 'Ошибка при подаче заявки');
    }
  };

  const handleEdit = (project) => {
    setEditingProject(project);
  };

  const handleSaveEdit = async (updatedProject) => {
    try {
      const token = localStorage.getItem('token');
      await axios.put(
        `http://localhost:3000/api/projects/${updatedProject.id}`,
        updatedProject,
        {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        }
      );
      
      setEditingProject(null);
      fetchProjects(); // Обновляем список проектов
      alert('Проект успешно обновлен!');
    } catch (error) {
      alert(error.response?.data?.error || 'Ошибка при обновлении проекта');
    }
  };

  const handleDeleteClick = (project) => {
    setProjectToDelete(project);
    setShowDeleteModal(true);
  };

  const handleDeleteConfirm = async () => {
    try {
      const token = localStorage.getItem('token');
      await axios.delete(
        `http://localhost:3000/api/projects/${projectToDelete.id}`,
        {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        }
      );
      
      setShowDeleteModal(false);
      setProjectToDelete(null);
      fetchProjects(); // Обновляем список проектов
      alert('Проект успешно удален!');
    } catch (error) {
      alert(error.response?.data?.error || 'Ошибка при удалении проекта');
    }
  };

  const getStatusText = (status) => {
  switch (status) {
    case 'DRAFT': return 'Черновик';
    case 'ACTIVE': return 'Активный';
    case 'COMPLETED': return 'Завершен';
    case 'CANCELLED': return 'Отменен';
    default: return status;
  }
};

  if (loading) {
    return <div className="loading">Загрузка проектов...</div>;
  }

  return (
    <div className="project-list">
      <h1>Волонтерские проекты</h1>
      
      <div className="projects-grid">
        {projects.map(project => (
          <div key={project.id} className="project-card">
            <h2>{project.title}</h2>
            <p>{project.description}</p>
            
            <div className="project-meta">
              <div className="meta-item">
                <strong>Создатель:</strong>
                <span>{project.creator.firstName} {project.creator.lastName}</span>
              </div>
              <div className="meta-item">
                <strong>Дата:</strong>
                <span>{new Date(project.createdAt).toLocaleDateString('ru-RU')}</span>
              </div>
              <div className="meta-item">
                <strong>Статус:</strong>
                <span className={`status-badge status-${project.status}`}>
                  {getStatusText(project.status)}
                </span>
              </div>
              <div className="meta-item">
                <strong>Заявки:</strong>
                <span>
                Всего: {project.applicationsCount} 
                {project.pendingApplicationsCount > 0 && 
               `, Новые: ${project.pendingApplicationsCount}`
                    }
                </span>
            </div>
            </div>

            <div className="project-actions">
              {user && user.role === 'volunteer' && (
                <button 
                  className="btn btn-primary"
                  onClick={() => handleApply(project.id)}
                >
                  📝 Подать заявку
                </button>
              )}
              
              {user && user.id === project.createdBy && (
                <>
                  <button 
                    className="btn btn-warning"
                    onClick={() => handleEdit(project)}
                  >
                    ✏️ Редактировать
                  </button>
                  <button 
                    className="btn btn-danger"
                    onClick={() => handleDeleteClick(project)}
                  >
                    🗑️ Удалить
                  </button>
                </>
              )}
              
              {user && user.id === project.createdBy && (
  <button 
    className="btn btn-success"
    onClick={() => window.location.href = `/project-applications/${project.id}`}
  >
    👥 Управление заявками 
    {project.pendingApplicationsCount > 0 && 
      ` (${project.pendingApplicationsCount} новых)`
    }
  </button>
)}
            </div>
          </div>
        ))}
      </div>

      {/* Модальное окно подтверждения удаления */}
      {showDeleteModal && (
        <div className="modal-overlay">
          <div className="modal-content">
            <div className="modal-header">
              <h2>Подтверждение удаления</h2>
              <button 
                className="close-btn"
                onClick={() => setShowDeleteModal(false)}
              >
                ×
              </button>
            </div>
            <p>Вы уверены, что хотите удалить проект "{projectToDelete?.title}"?</p>
            <p>Это действие нельзя отменить.</p>
            <div style={{ display: 'flex', gap: '1rem', marginTop: '1.5rem' }}>
              <button 
                className="btn btn-danger"
                onClick={handleDeleteConfirm}
              >
                Да, удалить
              </button>
              <button 
                className="btn"
                onClick={() => setShowDeleteModal(false)}
                style={{ backgroundColor: '#6c757d', color: 'white' }}
              >
                Отмена
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Модальное окно редактирования */}
      {editingProject && (
        <EditProjectModal 
          project={editingProject}
          onSave={handleSaveEdit}
          onCancel={() => setEditingProject(null)}
        />
      )}
    </div>
  );
}

// Компонент модального окна редактирования
function EditProjectModal({ project, onSave, onCancel }) {
  const [formData, setFormData] = useState({
    title: project.title,
    description: project.description,
    status: project.status
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    onSave({
      ...project,
      ...formData
    });
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <div className="modal-header">
          <h2>Редактировать проект</h2>
          <button className="close-btn" onClick={onCancel}>×</button>
        </div>
        
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Название проекта:</label>
            <input
              type="text"
              name="title"
              value={formData.title}
              onChange={handleChange}
              required
              style={{ width: '100%', padding: '0.5rem', marginBottom: '1rem' }}
            />
          </div>
          
          <div className="form-group">
            <label>Описание:</label>
            <textarea
              name="description"
              value={formData.description}
              onChange={handleChange}
              required
              rows="5"
              style={{ width: '100%', padding: '0.5rem', marginBottom: '1rem' }}
            />
          </div>

          <div className="form-group">
            <label>Статус:</label>
            <select
            name="status"
            value={formData.status}
            onChange={handleChange}
            style={{ width: '100%', padding: '0.5rem', marginBottom: '1rem' }}
            >
            <option value="DRAFT">Черновик</option>
            <option value="ACTIVE">Активный</option>
            <option value="COMPLETED">Завершен</option>
            <option value="CANCELLED">Отменен</option>
        </select>
          </div>
          
          <div style={{ display: 'flex', gap: '1rem' }}>
            <button type="submit" className="btn btn-primary">
              Сохранить
            </button>
            <button 
              type="button" 
              className="btn"
              onClick={onCancel}
              style={{ backgroundColor: '#6c757d', color: 'white' }}
            >
              Отмена
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default ProjectList;
