import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Profile.css';
import OrganizerStats from './OrganizerStats';
import DraftProjects from './DraftProjects';

function Profile({ user }) {
  const [activeTab, setActiveTab] = useState('profile');
  const [profile, setProfile] = useState(null);
  const [participationHistory, setParticipationHistory] = useState([]);
  const [loading, setLoading] = useState(false);
  const [editing, setEditing] = useState(false);
  const [formData, setFormData] = useState({});
  const [showDeleteConfirm, setShowDeleteConfirm] = useState(false);

  // –ò—Å–ø–æ–ª—å–∑—É–µ–º –¥–∞–Ω–Ω—ã–µ –∏–∑ user, –∫–æ—Ç–æ—Ä—ã–µ —É–∂–µ –µ—Å—Ç—å –ø–æ—Å–ª–µ –ª–æ–≥–∏–Ω–∞
  useEffect(() => {
    if (user) {
      // –°–æ–∑–¥–∞–µ–º –ø—Ä–æ—Ñ–∏–ª—å –∏–∑ –¥–∞–Ω–Ω—ã—Ö –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è, –∫–æ—Ç–æ—Ä—ã–µ —É–∂–µ –∑–∞–≥—Ä—É–∂–µ–Ω—ã
      const userProfile = {
        firstName: user.firstName,
        lastName: user.lastName,
        email: user.email,
        role: user.role,
        phone: '',
        skills: '',
        interests: '',
        bio: '',
        createdAt: new Date().toISOString()
      };
      setProfile(userProfile);
      setFormData(userProfile);
    }
  }, [user]);

  useEffect(() => {
    if (user && activeTab === 'history') {
      fetchParticipationHistory();
    }
  }, [activeTab, user]);

  const fetchParticipationHistory = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) return;

      const response = await axios.get('http://localhost:3000/api/profile/participation-history', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      setParticipationHistory(response.data);
    } catch (error) {
      console.error('–û—à–∏–±–∫–∞ –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ –∏—Å—Ç–æ—Ä–∏–∏ —É—á–∞—Å—Ç–∏—è:', error);
      // –ò–≥–Ω–æ—Ä–∏—Ä—É–µ–º –æ—à–∏–±–∫—É, –ø—Ä–æ—Å—Ç–æ –æ—Å—Ç–∞–≤–ª—è–µ–º –ø—É—Å—Ç–æ–π –º–∞—Å—Å–∏–≤
    }
  };

  const handleSaveProfile = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        alert('–¢—Ä–µ–±—É–µ—Ç—Å—è –∞–≤—Ç–æ—Ä–∏–∑–∞—Ü–∏—è');
        return;
      }

      const response = await axios.put('http://localhost:3000/api/profile', formData, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      
      // –û–±–Ω–æ–≤–ª—è–µ–º –¥–∞–Ω–Ω—ã–µ –≤ localStorage
      const updatedUser = {
        ...user,
        firstName: formData.firstName,
        lastName: formData.lastName
      };
      localStorage.setItem('user', JSON.stringify(updatedUser));
      
      setProfile(response.data || formData);
      setEditing(false);
      alert('–ü—Ä–æ—Ñ–∏–ª—å —É—Å–ø–µ—à–Ω–æ –æ–±–Ω–æ–≤–ª–µ–Ω!');
      
      // –û–±–Ω–æ–≤–ª—è–µ–º —Å—Ç—Ä–∞–Ω–∏—Ü—É, —á—Ç–æ–±—ã –ø—Ä–∏–º–µ–Ω–∏—Ç—å –∏–∑–º–µ–Ω–µ–Ω–∏—è
      window.location.reload();
    } catch (error) {
      console.error('–û—à–∏–±–∫–∞ –ø—Ä–∏ –æ–±–Ω–æ–≤–ª–µ–Ω–∏–∏ –ø—Ä–æ—Ñ–∏–ª—è:', error);
      // –î–∞–∂–µ –µ—Å–ª–∏ –±—ç–∫–µ–Ω–¥ –Ω–µ —Ä–∞–±–æ—Ç–∞–µ—Ç, —Å–æ—Ö—Ä–∞–Ω—è–µ–º –∏–∑–º–µ–Ω–µ–Ω–∏—è –ª–æ–∫–∞–ª—å–Ω–æ
      setProfile(formData);
      setEditing(false);
      alert('–ü—Ä–æ—Ñ–∏–ª—å –æ–±–Ω–æ–≤–ª–µ–Ω –ª–æ–∫–∞–ª—å–Ω–æ. –ò–∑–º–µ–Ω–µ–Ω–∏—è –º–æ–≥—É—Ç –±—ã—Ç—å –Ω–µ —Å–æ—Ö—Ä–∞–Ω–µ–Ω—ã –Ω–∞ —Å–µ—Ä–≤–µ—Ä–µ.');
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;   
    
    if (name === 'phone') {
      let cleanedValue = value.replace(/[^\d+]/g, '');
      
      if (cleanedValue.includes('+')) {
        const parts = cleanedValue.split('+');
        cleanedValue = '+' + parts.slice(1).join('').replace(/\D/g, '');
      }      
      
      if (cleanedValue.startsWith('+')) {
        if (cleanedValue.length > 12) {
          cleanedValue = cleanedValue.substring(0, 12);
        }
      } else {
        if (cleanedValue.length > 11) {
          cleanedValue = cleanedValue.substring(0, 11);
        }
      }      
      
      setFormData({
        ...formData,
        [name]: cleanedValue
      });
    } else {
      setFormData({
        ...formData,
        [name]: value
      });
    }
  };

  const generateCertificate = (project) => {
    if (!profile) {
      alert('–ù–µ —É–¥–∞–ª–æ—Å—å —Å–æ–∑–¥–∞—Ç—å —Å–µ—Ä—Ç–∏—Ñ–∏–∫–∞—Ç: –¥–∞–Ω–Ω—ã–µ –ø—Ä–æ—Ñ–∏–ª—è –Ω–µ –∑–∞–≥—Ä—É–∂–µ–Ω—ã');
      return;
    }

    const certificateText = `
      –°–ï–†–¢–ò–§–ò–ö–ê–¢ –í–û–õ–û–ù–¢–ï–†–ê
      –ù–∞—Å—Ç–æ—è—â–∏–º –ø–æ–¥—Ç–≤–µ—Ä–∂–¥–∞–µ—Ç—Å—è, —á—Ç–æ
      ${profile.firstName} ${profile.lastName}
      –ø—Ä–∏–Ω—è–ª(–∞) —É—á–∞—Å—Ç–∏–µ –≤ –ø—Ä–æ–µ–∫—Ç–µ:
      "${project.project.title}"
      –î–∞—Ç–∞ —É—á–∞—Å—Ç–∏—è: ${new Date(project.createdAt).toLocaleDateString('ru-RU')}
      –û—Ä–≥–∞–Ω–∏–∑–∞—Ç–æ—Ä: ${project.project.creator.firstName} ${project.project.creator.lastName}
      –ë–ª–∞–≥–æ–¥–∞—Ä–∏–º –∑–∞ –≤–∞—à –≤–∫–ª–∞–¥!  `;
    
    const blob = new Blob([certificateText], { type: 'text/plain' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `—Å–µ—Ä—Ç–∏—Ñ–∏–∫–∞—Ç_${project.project.title.replace(/\s+/g, '_')}.txt`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };


  // –ü—Ä–æ–≤–µ—Ä–∫–∏ –¥–æ–ª–∂–Ω—ã –∏–¥—Ç–∏ –ü–û–°–õ–ï –≤—Å–µ—Ö —Ö—É–∫–æ–≤
  if (!user) {
    return (
      <div className="error-container">
        <div className="error">
          <h2>–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏</h2>
