import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Profile.css';
import OrganizerStats from './OrganizerStats';
import DraftProjects from './DraftProjects';

function Profile({ user }) {
  const [activeTab, setActiveTab] = useState('profile');
  const [profile, setProfile] = useState(null);
  const [participationHistory, setParticipationHistory] = useState([]);
  const [loading, setLoading] = useState(true);
  const [editing, setEditing] = useState(false);
  const [formData, setFormData] = useState({});

  // Все хуки должны быть объявлены до любых условий
  useEffect(() => {
    if (user) {
      fetchProfile();
    } else {
      setLoading(false);
    }
  }, [user]);

  useEffect(() => {
    if (user && activeTab === 'history') {
      fetchParticipationHistory();
    }
  }, [activeTab, user]);

  const fetchProfile = async () => {
  try {
    const token = localStorage.getItem('token');
    console.log('Токен из localStorage:', token); // Отладочная информация
    
    if (!token) {
      console.error('Токен не найден в localStorage');
      setLoading(false);
      return;
    }

    console.log('Отправляем запрос на /api/profile...'); // Отладочная информация
    
    const response = await axios.get('http://localhost:3000/api/profile', {
      headers: {
        'Authorization': `Bearer ${token}`
      }
    });
    
    console.log('Профиль успешно загружен:', response.data); // Отладочная информация
    setProfile(response.data);
    setFormData(response.data);
    setLoading(false);
  } catch (error) {
    console.error('Полная ошибка при загрузке профиля:', error);
    console.error('Статус ошибки:', error.response?.status);
    console.error('Данные ошибки:', error.response?.data);
    console.error('URL запроса:', error.config?.url);
    setLoading(false);
  }
};
  const fetchParticipationHistory = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:3000/api/profile/participation-history', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      setParticipationHistory(response.data);
    } catch (error) {
      console.error('Ошибка при загрузке истории участия:', error);
    }
  };

  const handleSaveProfile = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      const response = await axios.put('http://localhost:3000/api/profile', formData, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      setProfile(response.data);
      setEditing(false);
      alert('Профиль успешно обновлен!');
    } catch (error) {
      console.error('Ошибка при обновлении профиля:', error);
      alert('Ошибка при обновлении профиля');
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    
    if (name === 'phone') {
      let cleanedValue = value.replace(/[^\d+]/g, '');
      
      if (cleanedValue.includes('+')) {
        const parts = cleanedValue.split('+');
        cleanedValue = '+' + parts.slice(1).join('').replace(/\D/g, '');
      }
      
      if (cleanedValue.startsWith('+')) {
        if (cleanedValue.length > 12) {
          cleanedValue = cleanedValue.substring(0, 12);
        }
      } else {
        if (cleanedValue.length > 11) {
          cleanedValue = cleanedValue.substring(0, 11);
        }
      }
      
      setFormData({
        ...formData,
        [name]: cleanedValue
      });
    } else {
      setFormData({
        ...formData,
        [name]: value
      });
    }
  };

  const generateCertificate = (project) => {
    const certificateText = `
      СЕРТИФИКАТ ВОЛОНТЕРА

      Настоящим подтверждается, что
      ${profile.firstName} ${profile.lastName}
      принял(а) участие в проекте:
      "${project.project.title}"

      Дата участия: ${new Date(project.createdAt).toLocaleDateString('ru-RU')}

      Организатор: ${project.project.creator.firstName} ${project.project.creator.lastName}

      Благодарим за ваш вклад!  `;

    const blob = new Blob([certificateText], { type: 'text/plain' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `сертификат_${project.project.title.replace(/\s+/g, '_')}.txt`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  // Проверки должны идти ПОСЛЕ всех хуков
  if (!user) {
    return <div className="loading">Загрузка данных пользователя...</div>;
  }

  if (loading) {
    return <div className="loading">Загрузка профиля...</div>;
  }

  if (!profile) {
    return <div className="error">Не удалось загрузить профиль</div>;
  }

  // Теперь мы уверены, что user и profile не null
  return (
    <div className="profile">
      <div className="profile-header">
        <h1>Личный кабинет</h1>
        {/* Убрали приветствие, так как оно теперь в навигации */}
      </div>

      <div className="profile-tabs-container">
        <div className="profile-tabs">
          <button
            className={`tab ${activeTab === 'profile' ? 'active' : ''}`}
            onClick={() => setActiveTab('profile')}
          >
            📝 Профиль
          </button>
          <button
            className={`tab ${activeTab === 'history' ? 'active' : ''}`}
            onClick={() => setActiveTab('history')}
          >
            📊 История участия
          </button>
          <button
            className={`tab ${activeTab === 'applications' ? 'active' : ''}`}
            onClick={() => setActiveTab('applications')}
          >
            📨 Мои заявки
          </button>
          
          {user && user.role === 'organizer' && (
            <button
              className={`tab ${activeTab === 'stats' ? 'active' : ''}`}
              onClick={() => setActiveTab('stats')}
            >
              📊 Статистика
            </button>
          )}
          
          {user && user.role === 'organizer' && (
            <button
              className={`tab ${activeTab === 'drafts' ? 'active' : ''}`}
              onClick={() => setActiveTab('drafts')}
            >
              📋 Черновики
            </button>
          )}
        </div>
      </div>

      <div className="profile-content">
        {activeTab === 'profile' && (
          <div className="profile-section">
            <div className="section-header">
              <h2>Личная информация</h2>
              {!editing ? (
                <button
                  className="btn btn-primary"
                  onClick={() => setEditing(true)}
                >
                  ✏️ Редактировать
                </button>
              ) : (
                <div className="edit-actions">
                  <button
                    className="btn btn-success"
                    onClick={handleSaveProfile}
                  >
                    💾 Сохранить
                  </button>
                  <button
                    className="btn btn-secondary"
                    onClick={() => {
                      setEditing(false);
                      setFormData(profile);
                    }}
                  >
                    ❌ Отмена
                  </button>
                </div>
              )}
            </div>

            {editing ? (
              <form className="profile-form">
                <div className="form-row">
                  <div className="form-group">
                    <label>Имя:</label>
                    <input
                      type="text"
                      name="firstName"
                      value={formData.firstName || ''}
                      onChange={handleInputChange}
                      required
                    />
                  </div>
                  <div className="form-group">
                    <label>Фамилия:</label>
                    <input
                      type="text"
                      name="lastName"
                      value={formData.lastName || ''}
                      onChange={handleInputChange}
                      required
                    />
                  </div>
                </div>

                <div className="form-group">
                  <label>Email:</label>
                  <input
                    type="email"
                    value={profile.email}
                    disabled
                    className="disabled-input"
                  />
                  <small>Email нельзя изменить</small>
                </div>

                <div className="form-group">
                  <label>Телефон:</label>
                  <input
                    type="tel"
                    name="phone"
                    value={formData.phone || ''}
                    onChange={handleInputChange}
                    placeholder="+7 (XXX) XXX-XX-XX"
                    pattern="^\+?[0-9]{0,11}$"
                    maxLength="12"
                    onKeyPress={(e) => {
                      if (!/[0-9+]/.test(e.key) && 
                          e.key !== 'Backspace' && 
                          e.key !== 'Delete' && 
                          e.key !== 'Tab') {
                        e.preventDefault();
                      }
                    }}
                  />
                  <small>Формат: +79991234567 (максимум 11 цифр после +)</small>
                </div>

                <div className="form-group">
                  <label>Навыки:</label>
                  <textarea
                    name="skills"
                    value={formData.skills || ''}
                    onChange={handleInputChange}
                    placeholder="Перечислите ваши навыки через запятую"
                    rows="3"
                  />
                </div>

                <div className="form-group">
                  <label>Интересы:</label>
                  <textarea
                    name="interests"
                    value={formData.interests || ''}
                    onChange={handleInputChange}
                    placeholder="Перечислите ваши интересы через запятую"
                    rows="3"
                  />
                </div>

                <div className="form-group">
                  <label>О себе:</label>
                  <textarea
                    name="bio"
                    value={formData.bio || ''}
                    onChange={handleInputChange}
                    placeholder="Расскажите о себе, своем опыте волонтерства"
                    rows="4"
                  />
                </div>
              </form>
            ) : (
              <div className="profile-info">
                <div className="info-grid">
                  <div className="info-item">
                    <strong>Имя:</strong>
                    <span>{profile.firstName} {profile.lastName}</span>
                  </div>
                  <div className="info-item">
                    <strong>Email:</strong>
                    <span>{profile.email}</span>
                  </div>
                  <div className="info-item">
                    <strong>Телефон:</strong>
                    <span>{profile.phone || 'Не указан'}</span>
                  </div>
                  <div className="info-item">
                    <strong>Навыки:</strong>
                    <span>{profile.skills || 'Не указаны'}</span>
                  </div>
                  <div className="info-item">
                    <strong>Интересы:</strong>
                    <span>{profile.interests || 'Не указаны'}</span>
                  </div>
                  <div className="info-item full-width">
                    <strong>О себе:</strong>
                    <span>{profile.bio || 'Не указано'}</span>
                  </div>
                  <div className="info-item">
                    <strong>Дата регистрации:</strong>
                    <span>{new Date(profile.createdAt).toLocaleDateString('ru-RU')}</span>
                  </div>
                </div>
              </div>
            )}
          </div>
        )}

        {activeTab === 'history' && (
          <div className="profile-section">
            <h2>История участия</h2>
            {participationHistory.length === 0 ? (
              <div className="empty-state">
                <p>У вас пока нет завершенных проектов</p>
                <p>Подавайте заявки на проекты и участвуйте в волонтерской деятельности!</p>
              </div>
            ) : (
              <div className="history-list">
                {participationHistory.map(participation => (
                  <div key={participation.id} className="history-item">
                    <div className="history-content">
                      <h3>{participation.project.title}</h3>
                      <p>{participation.project.description}</p>
                      <div className="history-meta">
                        <span>Организатор: {participation.project.creator.firstName} {participation.project.creator.lastName}</span>
                        <span>Дата участия: {new Date(participation.createdAt).toLocaleDateString('ru-RU')}</span>
                      </div>
                    </div>
                    <button
                      className="btn btn-primary"
                      onClick={() => generateCertificate(participation)}
                    >
                      📄 Скачать сертификат
                    </button>
                  </div>
                ))}
              </div>
            )}
          </div>
        )}

        {activeTab === 'applications' && (
          <div className="profile-section">
            <h2>Мои заявки</h2>
            <p>Для просмотра и управления вашими заявками перейдите в раздел "Мои заявки".</p>
            <button
              className="btn btn-primary"
              onClick={() => window.location.href = '/my-applications'}
            >
              📨 Перейти к моим заявкам
            </button>
          </div>
        )}

        {activeTab === 'stats' && (
          <div className="profile-section">
            <OrganizerStats user={user} />
          </div>
        )}

        {activeTab === 'drafts' && (
          <div className="profile-section">
            <DraftProjects user={user} />
          </div>
        )}
      </div>
    </div>
  );
}

export default Profile;
