import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Profile.css';
import OrganizerStats from './OrganizerStats';
import DraftProjects from './DraftProjects';

function Profile({ user }) {
  const [activeTab, setActiveTab] = useState('profile');
  const [profile, setProfile] = useState(null);
  const [participationHistory, setParticipationHistory] = useState([]);
  const [loading, setLoading] = useState(true);
  const [editing, setEditing] = useState(false);
  const [formData, setFormData] = useState({});

  // –í—Å–µ —Ö—É–∫–∏ –¥–æ–ª–∂–Ω—ã –±—ã—Ç—å –æ–±—ä—è–≤–ª–µ–Ω—ã –¥–æ –ª—é–±—ã—Ö —É—Å–ª–æ–≤–∏–π
  useEffect(() => {
    if (user) {
      fetchProfile();
    } else {
      setLoading(false);
    }
  }, [user]);

  useEffect(() => {
    if (user && activeTab === 'history') {
      fetchParticipationHistory();
    }
  }, [activeTab, user]);

  const fetchProfile = async () => {
  try {
    const token = localStorage.getItem('token');
    console.log('–¢–æ–∫–µ–Ω –∏–∑ localStorage:', token); // –û—Ç–ª–∞–¥–æ—á–Ω–∞—è –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è
    
    if (!token) {
      console.error('–¢–æ–∫–µ–Ω –Ω–µ –Ω–∞–π–¥–µ–Ω –≤ localStorage');
      setLoading(false);
      return;
    }

    console.log('–û—Ç–ø—Ä–∞–≤–ª—è–µ–º –∑–∞–ø—Ä–æ—Å –Ω–∞ /api/profile...'); // –û—Ç–ª–∞–¥–æ—á–Ω–∞—è –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è
    
    const response = await axios.get('http://localhost:3000/api/profile', {
      headers: {
        'Authorization': `Bearer ${token}`
      }
    });
    
    console.log('–ü—Ä–æ—Ñ–∏–ª—å —É—Å–ø–µ—à–Ω–æ –∑–∞–≥—Ä—É–∂–µ–Ω:', response.data); // –û—Ç–ª–∞–¥–æ—á–Ω–∞—è –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è
    setProfile(response.data);
    setFormData(response.data);
    setLoading(false);
  } catch (error) {
    console.error('–ü–æ–ª–Ω–∞—è –æ—à–∏–±–∫–∞ –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ –ø—Ä–æ—Ñ–∏–ª—è:', error);
    console.error('–°—Ç–∞—Ç—É—Å –æ—à–∏–±–∫–∏:', error.response?.status);
    console.error('–î–∞–Ω–Ω—ã–µ –æ—à–∏–±–∫–∏:', error.response?.data);
    console.error('URL –∑–∞–ø—Ä–æ—Å–∞:', error.config?.url);
    setLoading(false);
  }
};
  const fetchParticipationHistory = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:3000/api/profile/participation-history', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      setParticipationHistory(response.data);
    } catch (error) {
      console.error('–û—à–∏–±–∫–∞ –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ –∏—Å—Ç–æ—Ä–∏–∏ —É—á–∞—Å—Ç–∏—è:', error);
    }
  };

  const handleSaveProfile = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      const response = await axios.put('http://localhost:3000/api/profile', formData, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      setProfile(response.data);
      setEditing(false);
      alert('–ü—Ä–æ—Ñ–∏–ª—å —É—Å–ø–µ—à–Ω–æ –æ–±–Ω–æ–≤–ª–µ–Ω!');
    } catch (error) {
      console.error('–û—à–∏–±–∫–∞ –ø—Ä–∏ –æ–±–Ω–æ–≤–ª–µ–Ω–∏–∏ –ø—Ä–æ—Ñ–∏–ª—è:', error);
      alert('–û—à–∏–±–∫–∞ –ø—Ä–∏ –æ–±–Ω–æ–≤–ª–µ–Ω–∏–∏ –ø—Ä–æ—Ñ–∏–ª—è');
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    
    if (name === 'phone') {
      let cleanedValue = value.replace(/[^\d+]/g, '');
      
      if (cleanedValue.includes('+')) {
        const parts = cleanedValue.split('+');
        cleanedValue = '+' + parts.slice(1).join('').replace(/\D/g, '');
      }
      
      if (cleanedValue.startsWith('+')) {
        if (cleanedValue.length > 12) {
          cleanedValue = cleanedValue.substring(0, 12);
        }
      } else {
        if (cleanedValue.length > 11) {
          cleanedValue = cleanedValue.substring(0, 11);
        }
      }
      
      setFormData({
        ...formData,
        [name]: cleanedValue
      });
    } else {
      setFormData({
        ...formData,
        [name]: value
      });
    }
  };

  const generateCertificate = (project) => {
    const certificateText = `
      –°–ï–†–¢–ò–§–ò–ö–ê–¢ –í–û–õ–û–ù–¢–ï–†–ê

      –ù–∞—Å—Ç–æ—è—â–∏–º –ø–æ–¥—Ç–≤–µ—Ä–∂–¥–∞–µ—Ç—Å—è, —á—Ç–æ
      ${profile.firstName} ${profile.lastName}
      –ø—Ä–∏–Ω—è–ª(–∞) —É—á–∞—Å—Ç–∏–µ –≤ –ø—Ä–æ–µ–∫—Ç–µ:
      "${project.project.title}"

      –î–∞—Ç–∞ —É—á–∞—Å—Ç–∏—è: ${new Date(project.createdAt).toLocaleDateString('ru-RU')}

      –û—Ä–≥–∞–Ω–∏–∑–∞—Ç–æ—Ä: ${project.project.creator.firstName} ${project.project.creator.lastName}

      –ë–ª–∞–≥–æ–¥–∞—Ä–∏–º –∑–∞ –≤–∞—à –≤–∫–ª–∞–¥!  `;

    const blob = new Blob([certificateText], { type: 'text/plain' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `—Å–µ—Ä—Ç–∏—Ñ–∏–∫–∞—Ç_${project.project.title.replace(/\s+/g, '_')}.txt`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  // –ü—Ä–æ–≤–µ—Ä–∫–∏ –¥–æ–ª–∂–Ω—ã –∏–¥—Ç–∏ –ü–û–°–õ–ï –≤—Å–µ—Ö —Ö—É–∫–æ–≤
  if (!user) {
    return <div className="loading">–ó–∞–≥—Ä—É–∑–∫–∞ –¥–∞–Ω–Ω—ã—Ö –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è...</div>;
  }

  if (loading) {
    return <div className="loading">–ó–∞–≥—Ä—É–∑–∫–∞ –ø—Ä–æ—Ñ–∏–ª—è...</div>;
  }

  if (!profile) {
    return <div className="error">–ù–µ —É–¥–∞–ª–æ—Å—å –∑–∞–≥—Ä—É–∑–∏—Ç—å –ø—Ä–æ—Ñ–∏–ª—å</div>;
  }

  // –¢–µ–ø–µ—Ä—å –º—ã —É–≤–µ—Ä–µ–Ω—ã, —á—Ç–æ user –∏ profile –Ω–µ null
  return (
    <div className="profile">
      <div className="profile-header">
        <h1>–õ–∏—á–Ω—ã–π –∫–∞–±–∏–Ω–µ—Ç</h1>
        {/* –£–±—Ä–∞–ª–∏ –ø—Ä–∏–≤–µ—Ç—Å—Ç–≤–∏–µ, —Ç–∞–∫ –∫–∞–∫ –æ–Ω–æ —Ç–µ–ø–µ—Ä—å –≤ –Ω–∞–≤–∏–≥–∞—Ü–∏–∏ */}
      </div>

      <div className="profile-tabs-container">
        <div className="profile-tabs">
          <button
            className={`tab ${activeTab === 'profile' ? 'active' : ''}`}
            onClick={() => setActiveTab('profile')}
          >
            üìù –ü—Ä–æ—Ñ–∏–ª—å
          </button>
          <button
            className={`tab ${activeTab === 'history' ? 'active' : ''}`}
            onClick={() => setActiveTab('history')}
          >
            üìä –ò—Å—Ç–æ—Ä–∏—è —É—á–∞—Å—Ç–∏—è
          </button>
          <button
            className={`tab ${activeTab === 'applications' ? 'active' : ''}`}
            onClick={() => setActiveTab('applications')}
          >
            üì® –ú–æ–∏ –∑–∞—è–≤–∫–∏
          </button>
          
          {user && user.role === 'organizer' && (
            <button
              className={`tab ${activeTab === 'stats' ? 'active' : ''}`}
              onClick={() => setActiveTab('stats')}
            >
              üìä –°—Ç–∞—Ç–∏—Å—Ç–∏–∫–∞
            </button>
          )}
          
          {user && user.role === 'organizer' && (
            <button
              className={`tab ${activeTab === 'drafts' ? 'active' : ''}`}
              onClick={() => setActiveTab('drafts')}
            >
              üìã –ß–µ—Ä–Ω–æ–≤–∏–∫–∏
            </button>
          )}
        </div>
      </div>

      <div className="profile-content">
        {activeTab === 'profile' && (
          <div className="profile-section">
            <div className="section-header">
              <h2>–õ–∏—á–Ω–∞—è –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è</h2>
              {!editing ? (
                <button
                  className="btn btn-primary"
                  onClick={() => setEditing(true)}
                >
                  ‚úèÔ∏è –†–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞—Ç—å
                </button>
              ) : (
                <div className="edit-actions">
                  <button
                    className="btn btn-success"
                    onClick={handleSaveProfile}
                  >
                    üíæ –°–æ—Ö—Ä–∞–Ω–∏—Ç—å
                  </button>
                  <button
                    className="btn btn-secondary"
                    onClick={() => {
